var i18n_lang;
var getLanguageByBrowser = function() {
 var userLang = navigator.language || navigator.userLanguage;
 userLang = userLang.toLowerCase();
 switch(userLang) {
    case 'ja-jp':
    case 'ja':
        return 'ja'
    case 'zh-tw':
        return 'zh-tw'
    default:
        return 'en'
 }
}

function getLanguage() {
    var regex = new RegExp("[?&]next(=([^&#]*)|&|#|$)"),
    results = regex.exec(window.location.href);
    if (!results) return window.location.pathname.split('/')[1];
    if (!results[2]) return window.location.pathname.split('/')[1];
    return decodeURIComponent(results[2].replace(/\+/g, " ").split('/')[1])
}

$(document).ready(function () {
    var locallang = getLanguage()
    if (locallang == "zh-tw") {
        i18n_lang = zhtw;
    }
    else if (locallang == "en") {
        i18n_lang = enus;
    }
    else if (locallang == "ja") {
        i18n_lang = jajp;
    }
    else {
        i18n_lang = enus;
    }
    $("*[data-i18n]").each(function () {
        var element = $(this);
        var data = element.data();
        var param = {};
        for (var key in data) {
            if (key.indexOf('i18npara_') == 0) {
                param[key.substr(9)] = data[key];
            }
            if (key.indexOf('i18nattr_') == 0) {
                element.attr(key.substr(9), i18n_lang(data[key]))
            }
        }
        if (!!element.data("i18n")) {
            element.html(i18n_lang(element.data("i18n"), param));
        }
        element.attr('data-i18ncomplete', element.attr('data-i18n'));
        element.removeAttr("data-i18n");
    });
});